<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Details extends CI_Controller {	function __construct(){		session_start();		parent::__construct();		$lang = $this->session->userdata('lang');		if($lang=='ch'){			$this->session->set_userdata('lang','ch');			$this->langtype='_ch';			$this->lang->load('gksel','chinese');		}else{			$this->session->set_userdata('lang','en');			$this->langtype='_en';			$this->lang->load('gksel','english');		}	}	public function index(){		$this->session->set_userdata('menu', 'details');		$data['website_title'] = 'Rojo Clothing';		$this->load->view('default/details', $data);	}		function order_recommend(){		$this->load->view('default/order_recommend');	}}/* End of file welcome.php *//* Location: ./application/controllers/welcome.php */