<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class GoShop extends CI_Controller {	function __construct(){		session_start();		parent::__construct();		$lang = $this->session->userdata('lang');		if($lang=='ch'){			$this->session->set_userdata('lang','ch');			$this->langtype='_ch';			$this->lang->load('gksel','chinese');		}else{			$this->session->set_userdata('lang','en');			$this->langtype='_en';			$this->lang->load('gksel','english');		}	}	public function index(){		$this->session->set_userdata('menu', 'goshop');		$data['website_title'] = 'Rojo Clothing';		$this->load->view('default/goshop', $data);	}		function order_list(){		$this->load->view('default/order_list');	}	function goshop_3d(){		$this->load->view('default/goshop_3d');	}	function goshop_switch(){		$this->load->view('default/goshop_switch');	}}/* End of file welcome.php *//* Location: ./application/controllers/welcome.php */