<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Service extends CI_Controller {	function __construct(){		session_start();		parent::__construct();		$lang = $this->session->userdata('lang');		if($lang=='ch'){			$this->session->set_userdata('lang','ch');			$this->langtype='_ch';			$this->lang->load('gksel','chinese');		}else{			$this->session->set_userdata('lang','en');			$this->langtype='_en';			$this->lang->load('gksel','english');		}		$this->session->set_userdata('menu', 'service');	}	public function index(){		redirect('service/thefleet');	}	function info($shorturl){		$this->session->set_userdata('submenu', 'service_'.$shorturl);		$data['cmsinfo'] = $this->CmsModel->getcmsinfo_BYshorturl($shorturl);			$con=array('parent'=>54,'orderby'=>'a.cms_id','orderby_res'=>'ASC');		$data['cmslist'] = $this->CmsModel->getcmslist($con);				$data['website_title'] = 'Rojo Clothing';		$this->load->view('default/service_thefleet', $data);	}	}/* End of file welcome.php *//* Location: ./application/controllers/welcome.php */