<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Shop extends CI_Controller {	function __construct(){		session_start();		parent::__construct();		$lang = $this->session->userdata('lang');		if($lang=='ch'){			$this->session->set_userdata('lang','ch');			$this->langtype='_ch';			$this->lang->load('gksel','chinese');		}else{			$this->session->set_userdata('lang','en');			$this->langtype='_en';			$this->lang->load('gksel','english');		}	}	public function index(){		$this->session->set_userdata('menu', 'shop');// 		$data['website_title'] = 'Rojo Clothing';// 		$this->load->view('default/shop_list', $data);		redirect('shop/category/suit');	}		function accessories_list(){		$this->session->set_userdata('menu', 'shop');		$data['website_title'] = 'Rojo Clothing';				// 		$con=array('parent'=>20, 'orderby'=>'a.sort', 'orderby_res'=>'ASC');// 		$data['subcategorylist']=$this->ProductModel->getproductcategorylist($con);		// 		$this->load->view('default/shop_accessories_list', $data);		redirect('shop/accessories/ties');	}		function category($shorturl){		$this->session->set_userdata('submenu', 'category_'.$shorturl);		$data['website_title'] = 'Rojo Clothing';			$con=array('parent'=>3, 'orderby'=>'a.sort', 'orderby_res'=>'ASC');		$data['subcategorylist']=$this->ProductModel->getproductcategorylist($con);			$data['subcategoryinfo'] = $this->ProductModel->getproductcategoryinfo_BYshorturl($shorturl);			$con=array('category_id'=>$data['subcategoryinfo']['category_id'],'orderby'=>'a.product_id','orderby_res'=>'DESC');		$data['productlist']=$this->ProductModel->getproductlist($con);			$this->load->view('default/shop_category_sub_list', $data);	}		function accessories($shorturl){		$this->session->set_userdata('submenu', 'accessories_'.$shorturl);		$data['website_title'] = 'Rojo Clothing';				$con=array('parent'=>20, 'orderby'=>'a.sort', 'orderby_res'=>'ASC');		$data['subcategorylist']=$this->ProductModel->getproductcategorylist($con);				$data['subcategoryinfo'] = $this->ProductModel->getproductcategoryinfo_BYshorturl($shorturl);				$con=array('category_id'=>$data['subcategoryinfo']['category_id'],'orderby'=>'a.product_id','orderby_res'=>'DESC');		$data['productlist']=$this->ProductModel->getproductlist($con);				$this->load->view('default/shop_accessories_sub_list', $data);	}		function productinfo($product_id){		$data['website_title'] = 'Rojo Clothing';			$con=array('parent'=>20, 'orderby'=>'a.sort', 'orderby_res'=>'ASC');		$data['subcategorylist']=$this->ProductModel->getproductcategorylist($con);			$data['productinfo'] = $this->ProductModel->getproductinfo($product_id);			$con=array('product_id'=>$product_id, 'orderby'=>'a.sort', 'orderby_res'=>'ASC');		$data['picturelist']=$this->ProductModel->getpicturelist($con);			$this->load->view('default/product_info', $data);	}		function goshop(){		$this->load->view('default/goshop');	}	function shop_suit(){		$this->session->set_userdata('submenu', 'shop_suit');		$this->load->view('default/shop_suit');	}	function shop_blazer(){		$this->session->set_userdata('submenu', 'shop_blazer');		$this->load->view('default/shop_blazer');	}	function shop_trousers(){		$this->session->set_userdata('submenu', 'shop_trousers');		$this->load->view('default/shop_trousers');	}	function shop_waistcoat(){		$this->session->set_userdata('submenu', 'shop_waistcoat');		$this->load->view('default/shop_waistcoat');	}	function shop_black(){		$this->session->set_userdata('submenu', 'shop_black');		$this->load->view('default/shop_black');	}}/* End of file welcome.php *//* Location: ./application/controllers/welcome.php */