<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Trousers extends CI_Controller {	function __construct(){		session_start();		parent::__construct();		$lang = $this->session->userdata('lang');		if($lang=='ch'){			$this->session->set_userdata('lang','ch');			$this->langtype='_ch';			$this->lang->load('gksel','chinese');		}else{			$this->session->set_userdata('lang','en');			$this->langtype='_en';			$this->lang->load('gksel','english');		}	}	public function index(){		$this->session->set_userdata('menu', 'brands');		$data['website_title'] = 'Rojo Clothing';		$this->load->view('default/trousers', $data);	}	 	function product(){		$this->load->view('default/product');	}			}/* End of file welcome.php *//* Location: ./application/controllers/welcome.php */