e[i].value>0){				picid=allpicture[i].id;				picid_split=picid.split('_');				details.push(parseInt(picid_split[1]));			}			srcarr=srcarr+'"'+baseurl+allpicturesrc[i].value+'", ';		}	}	// 当前的热点--第几个	var detailIndex = 0;	// initialise spritespin	var spin = $('#spritespin');	spin.spritespin({		source: [			 				      "<?php echo base_url().'themes/default/images/goshop3d_1.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_2.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_3.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_4.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_5.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_6.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_7.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_8.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_9.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_10.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_11.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_12.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_13.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_14.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_15.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_16.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_17.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_18.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_19.png'?>",			    			      "<?php echo base_url().'themes/default/images/goshop3d_20.png'?>",			    			   			    						    			    ],	    width: 1000,	    sense: -1,	    height: 450,	    animate: false	});	// get the api object. This is used to trigger animation to play up to a specific frame	var api = spin.spritespin("api");	spin.bind("onLoad", function(){	    var data = api.data;	    data.stage.prepend($(".details .detail")); // add current details	    data.stage.find(".detail").hide();         // hide current details	}).bind("onFrame", function(){	    var data = api.data;	    data.stage.find(".detail:visible").stop(false).fadeOut();	    $('.allgifshow').fadeOut();	    data.stage.find(".detail.detail-" + data.frame).stop(false).fadeIn();	});    	$( "#prev" ).click(function(){	    setDetailIndex(detailIndex - 1);	});    	$( "#next" ).click(function(){	    setDetailIndex(detailIndex + 1);	});	function setDetailIndex(index){	    detailIndex = index;	    if (detailIndex < 0){	        detailIndex = details.length - 1;	    }	    if (detailIndex >= details.length){	        detailIndex = 0;	    }	    api.playTo(details[detailIndex]);	}}</script>