<?php $this->load->view('default/qrcode_header');?><div style="margin: 0 auto;width:100%;max-width:640px;">	<div style="float: left;width:80%;margin-left:10%;margin-top:10%;">			<div style="float: left;width:100%;margin-top:10px;">				<input style="float: left;width:calc(100% - 4px);" type="text" name="password" placeholder="Password"/>			</div>			<div class="passworderror" style="display:none;float: left;width:100%;margin-top:10px;color:red;">				Password can not be empty			</div>			<div class="passworderror_error" style="display:none;float: left;width:100%;margin-top:10px;color:red;">				Password error			</div>						<div style="float: left;width:100%;margin-top:10px;">				<div class="btn_product_submit" onclick="tosubmitproduct_login()" style="float: left;width:100%;color: #fff;text-align:center;background: #111d35;height:45px;line-height:45px;font-size: 14px;border-radius: 4px;">					Submit				</div>				<div class="btn_product_loading" style="display:none;float: left;width:100%;color: #fff;background: #111d35;height:45px;line-height:45px;font-size: 14px;border-radius: 4px;">					<div style="float:left;width:48%;"><img style="float:right;margin-right:5px;margin-top:13px;width:18px;height:18px;" src="<?php echo base_url().'themes/default/images/ajax_loading.gif'?>"/></div>					<div style="float:left;width:52%;text-align:left;">Loading...</div>				</div>				<div class="btn_product_success" style="display:none;float: left;width:100%;color: #fff;background: #111d35;height:45px;line-height:45px;font-size: 14px;border-radius: 4px;">					<div style="float:left;width:48%;"><img style="float:right;margin-right:5px;margin-top:13px;width:18px;height:18px;" src="<?php echo base_url().'themes/default/images/global_ok.png'?>"/></div>					<div style="float:left;width:52%;text-align:left;">Success</div>				</div>			</div>	</div></div><script type="text/javascript">function tosubmitproduct_login(){	$('.btn_product_submit').hide();	$('.btn_product_loading').show();	$('.btn_product_success').hide();	var password = $('input[name="password"]').val();	//验证	var ispass = 1;	if(isNull.test(password)){ispass=0;$('.passworderror').show();}else{$('.passworderror').hide();}	$('.passworderror_error').hide();	if(ispass == 1){		$.post(baseurl+'index.php/product/tosubmitproduct_login/<?php echo $productinfo['product_id']?>', {password:password}, function (data){			if(data == 'yes'){				$('.btn_product_submit').hide();				$('.btn_product_loading').hide();				$('.btn_product_success').show();				location.href = baseurl+"index.php/product/toactionstatus?product_id=<?php echo $productinfo['product_id']?>&product_key=<?php echo $productinfo['product_key']?>";			}else{				$('.btn_product_submit').show();				$('.btn_product_loading').hide();				$('.btn_product_success').hide();				$('.passworderror_error').show();			}		})	}else{		$('.btn_product_submit').show();		$('.btn_product_loading').hide();		$('.btn_product_success').hide();	}	}
//打开或关闭日历function toggle_calendar(id){	var isopten=$('#'+id).attr('isopten');	if(isopten==0){		var default_val=$('#'+id).val();		$.post(baseurl+'index.php/welcome/calendar_select',{id:id,default_val:default_val},function (data){			$('#'+id+'_area').html(data);			$('#'+id).attr('isopten',1);		});	}else{		$('#'+id+'_area').html('');		$('#'+id).attr('isopten',0);	}}var currentshiwudi = '';function togetrilidatatoinput(id,year,month,day){	if(day<10){		var day_show='0'+day;	}else{		var day_show=day;	}	$('#'+id).val(year+'-'+month+'-'+day_show);	toggle_calendar(id);	if(currentshiwudi != ''){		$('#shiwu_'+currentshiwudi).find('font').css({'color':'#1F9237'});	}	$('#shiwu_'+year+'_'+month+'_'+day).find('font').css({'color':'red'});	currentshiwudi = year+'_'+month+'_'+day;}function togetshiwucalendar_month(id,year,month){	$.post(baseurl+'index.php/welcome/calendar_select/'+year+'/'+month,{id:id},function (data){		$('#'+id+'_area').html(data);	});}
</script><?php $this->load->view('default/qrcode_footer');?>