ct/tocheckusernumber', {user_number:user_number}, function (data){			var obj = eval( "(" + data + ")" );			if(obj.status == 'yes'){				$('.user_numbererror_error').hide();			}else{				$('.user_numbererror_error').show();			}		})	}}				function tosubmitproduct_step1(){	$('.btn_product_submit').hide();	$('.btn_product_loading').show();	$('.btn_product_success').hide();	var user_number = $('input[name="user_number"]').val();	var date_cloth_due = $('input[name="date_cloth_due"]').val();	var date_cloth_submitted = $('input[name="date_cloth_submitted"]').val();	var factory_id = $('select[name="factory_id"]').val();	//验证	var ispass = 1;	if(isNull.test(user_number)){ispass=0;$('.user_numbererror').show();}else{$('.user_numbererror').hide();}	if(isNull.test(date_cloth_due)){ispass=0;$('.date_cloth_dueerror').show();}else{$('.date_cloth_dueerror').hide();}	if(isNull.test(date_cloth_submitted)){ispass=0;$('.date_cloth_submittederror').show();}else{$('.date_cloth_submittederror').hide();}		$('.user_numbererror_error').hide();	if(ispass == 1){		$.post(baseurl+'index.php/product/tosubmitproduct_step1/<?php echo $productinfo['product_id']?>', {user_number:user_number, factory_id:factory_id, date_cloth_due:date_cloth_due, date_cloth_submitted:date_cloth_submitted}, function (data){			if(data == 'yes'){				$('.btn_product_submit').hide();				$('.btn_product_loading').hide();				$('.btn_product_success').show();				//location.href = baseurl+"index.php/product/toactionstatus?product_id=<?php //echo $productinfo['product_id']?>&product_key=<?php //echo $productinfo['product_key']?>";			}else{				$('.btn_product_submit').show();				$('.btn_product_loading').hide();				$('.btn_product_success').hide();				$('.user_numbererror_error').show();			}		})	}else{		$('.btn_product_submit').show();		$('.btn_product_loading').hide();		$('.btn_product_success').hide();	}	}function tosubmitproduct_step1_other(){	$('.btn_product_submit').hide();	$('.btn_product_loading').show();	$('.btn_product_success').hide();	$.post(baseurl+'index.php/product/tosubmitproduct_step1_other/<?php echo $productinfo['product_id']?>', function (data){		$('.btn_product_submit').hide();		$('.btn_product_loading').hide();		$('.btn_product_success').show();		//location.href = baseurl+"index.php/product/toactionstatus?product_id=<?php //echo $productinfo['product_id']?>&product_key=<?php //echo $productinfo['product_key']?>";	})	}
//打开或关闭日历function toggle_calendar(id){	var isopten=$('#'+id).attr('isopten');	if(isopten==0){		var default_val=$('#'+id).val();		$.post(baseurl+'index.php/welcome/calendar_select',{id:id,default_val:default_val},function (data){			$('#'+id+'_area').html(data);			$('#'+id).attr('isopten',1);		});	}else{		$('#'+id+'_area').html('');		$('#'+id).attr('isopten',0);	}}var currentshiwudi = '';function togetrilidatatoinput(id,year,month,day){	if(day<10){		var day_show='0'+day;	}else{		var day_show=day;	}	$('#'+id).val(year+'-'+month+'-'+day_show);	toggle_calendar(id);	if(currentshiwudi != ''){		$('#shiwu_'+currentshiwudi).find('font').css({'color':'#1F9237'});	}	$('#shiwu_'+year+'_'+month+'_'+day).find('font').css({'color':'red'});	currentshiwudi = year+'_'+month+'_'+day;}function togetshiwucalendar_month(id,year,month){	$.post(baseurl+'index.php/welcome/calendar_select/'+year+'/'+month,{id:id},function (data){		$('#'+id+'_area').html(data);	});}
</script><?php $this->load->view('default/qrcode_footer');?>