>&product_key=<?php //echo $productinfo['product_key']?>";		})	}else{		$('.btn_product_submit').show();		$('.btn_product_loading').hide();		$('.btn_product_success').hide();	}	}//打开或关闭日历function toggle_calendar(id){	var isopten=$('#'+id).attr('isopten');	if(isopten==0){		var default_val=$('#'+id).val();		$.post(baseurl+'index.php/welcome/calendar_select',{id:id,default_val:default_val},function (data){			$('#'+id+'_area').html(data);			$('#'+id).attr('isopten',1);		});	}else{		$('#'+id+'_area').html('');		$('#'+id).attr('isopten',0);	}}var currentshiwudi = '';function togetrilidatatoinput(id,year,month,day){	if(day<10){		var day_show='0'+day;	}else{		var day_show=day;	}	$('#'+id).val(year+'-'+month+'-'+day_show);	toggle_calendar(id);	if(currentshiwudi != ''){		$('#shiwu_'+currentshiwudi).find('font').css({'color':'#1F9237'});	}	$('#shiwu_'+year+'_'+month+'_'+day).find('font').css({'color':'red'});	currentshiwudi = year+'_'+month+'_'+day;}function togetshiwucalendar_month(id,year,month){	$.post(baseurl+'index.php/welcome/calendar_select/'+year+'/'+month,{id:id},function (data){		$('#'+id+'_area').html(data);	});}</script><?php $this->load->view('default/qrcode_footer');?>